<?phpnamespace app\controllers;use yii\web\Controller;use yii\VideoCloud\Storage\UploadManager;use lib\models\VipVideo;use Yii;class AutodbController extends Controller{    public function actionUpload()    {        //到本地拿一条数据        $model = VipVideo::findOne(['upload_status'=>1,'upload_status_image'=>1,'sql_status'=>0]);        if($model)        {            //查线上是否有这条记录            $result = Yii::$app->dbcn->createCommand("select id from vip_video where id={$model->id}")->queryOne();            if($result) {                $model->sql_status = 1;                $model->save();                echo 'ok';die;                die;            }            $sql = "INSERT INTO vip_video(id,title,description,price,vip_price,cover,thumbnail,wy_name,wy_vid,upload_status)  VALUES('{$model->id}','{$model->title}','{$model->description}','{$model->price}',  '{$model->vip_price}','{$model->cover_string}','{$model->thumbnail_json}','{$model->wy_name}','{$model->wy_vid}',1)";            if(Yii::$app->dbcn->createCommand($sql)->execute()) {                $model->sql_status = 1;                $model->save();                echo 'insert into db';            }        }    }}