<?phpnamespace app\controllers;use lib\models\Agent;use yii;//直播频道控制器class ChannelController extends TemplateController{    public $config = [        'modelName' => 'lib\models\Channel',        'modelShortName' => 'Channel',        'listUrl' => 'Channel',        'addUrl' => '',        'delUrl' => 'ChannelDel',        'chgUrl' => '',        'addTitle' => '添加',        'addFormName' => '参数',        'chgTitle' => '修改',        'chgFormName' => '参数',        'listTitle' => '标签列表',        'listFormName' => '参数',    ];    //可搜索的选项    public function getSearch()    {        return [        ];    }    //添加修改中定主的字段    protected function getOption()    {        return [        ];    }    //主页中显示的字段名    public function getIndexField()    {        return [            'iid',            'name',        ];    }    public function actionDel()    {        $id = $this->post('data');        if( empty($id) ){            $this->error(\Yii::t('app', 'error'));        }        $query = $this->config['modelName']::findOne($id);        $cid = '';        if($query) {            $cid = $query->cid;        }        if($query && $query->delete())        {            if($cid) {                \lib\wyim\channel::deleteChanel($cid);            }            $this->success(\Yii::t('app', 'success'));        }        else        {            $this->error(\Yii::t('app', 'fail'));        }    }}?>