<?phpnamespace app\controllers;use lib\models\MemberPrice;use lib\models\Member;use yii;//会员等级价格设置class MemberpriceController extends TemplateController{    public $config = [        'modelName' => 'lib\models\MemberPrice',        'modelShortName' => 'MemberPrice',        'listUrl' => 'MemberPrice',        'addUrl' => 'MemberPricelAdd',        'delUrl' => 'MemberPricelDel',        'chgUrl' => 'MemberPricelEdit',        'addTitle' => '添加',        'addFormName' => '参数',        'chgTitle' => '修改',        'chgFormName' => '参数',        'listTitle' => '标签列表',        'listFormName' => '参数',    ];    //可搜索的选项    public function getSearch()    {        return [        ];    }    //添加修改中定主的字段    protected function getOption()    {        $member_array = Member::getCacheList();        $list = [];        foreach($member_array as $row) {            $list[$row['iid']] =  $row['name'];        }        return [            ['key'=>'member_id','value'=>'0','html'=>'select','option'=>$list],            ['key'=>'mos','value'=>'','html'=>'text','option'=>''],            ['key'=>'price','value'=>'','html'=>'text','option'=>'','notes'=>'(每月)'],            ['key'=>'notes','value'=>'','html'=>'text','option'=>''],        ];    }    //主页中显示的字段名    public function getIndexField()    {        return [            'iid',            'member_id',            'mos',            'price',            'notes',        ];    }}?>