   </div>';        $("#addFile").click(function(){            i++;            var htmlstr = str.replace('$i',i);            $("#form_content").append(htmlstr);        })        $("#submit").click(function(){            loadindex = layer.load('上传中', {shade: false});        })        $("#form_content").on('change', '.videofile',function(){            var url = $(this).val();            url = url.substring(url.lastIndexOf("\\")+1,url.lastIndexOf("."));            $(this).prev().prev().val(url);        })    });</script>