<?phpnamespace api\controllers;use Yii;use lib\models\ActivityReward;//活动类class ActivityController extends BasisController{    public function actionWatchlive()    {        $userId = Yii::$app->factory->getuser()->userId;            }    //领取活动奖励    public function actionReceiveRewards()    {               $type = Yii::$app->getRequest()->post('event');        ActivityReward::$userModel = Yii::$app->factory->getuser();        $rst = ActivityReward::get($type);        $activity = ActivityReward::getUnused($type);        $this->success(['rst'=>$rst,'activity'=>$activity]);    }    //激活一个奖励    public function actionActivation()    {        $type = Yii::$app->getRequest()->post('event');        ActivityReward::$userModel = Yii::$app->factory->getuser();        $rst = ActivityReward::activation($type);        $this->success($rst);    }}