<?phpnamespace api\controllers;use Yii;//好友分组控制器class BuddyGroupController extends BasisController{    public $formName = '\lib\forms\BuddyGroupForm';    public function actionAdd()    {        parent::baseAction($this->formName, 'add');    }    public function actionUpdate()    {        parent::baseAction($this->formName, 'update');    }    public function actionMove()    {        parent::baseAction($this->formName, 'move');    }    public function actionDelete()    {        parent::baseAction($this->formName, 'delete');    }        public function actionList()    {        $userId = Yii::$app->factory->getuser()->userId;        $buddy_id = Yii::$app->getRequest()->post('buddy_id');        $data = \lib\models\BuddyGroup::getNoBuddyList( $userId );        if($buddy_id) {            $buddyModel = \lib\models\Buddy::findOne(['user_id'=>$userId, 'buddy_id'=>$buddy_id]);            if(!$buddyModel) {                $this->error('该好友不存在');            }            foreach($data as $key => $row) {                if($row['iid'] == $buddyModel->group_id) {                    $index = $key;                    break;                }            }            $this->success( ['index'=>$index ?? 0, 'list'=>$data] );        } else {            $this->success( ['list'=>$data] );        }    }    public function actionMovebuddy()    {            }}