<?phpnamespace api\controllers;use Yii;//好友分组控制器class BuddyGroupController extends BasisController{    public $formName = '\lib\forms\BuddyGroupForm';    public function actionAdd()    {        parent::baseAction($this->formName, 'add');    }    public function actionUpdate()    {        parent::baseAction($this->formName, 'update');    }    public function actionMove()    {        parent::baseAction($this->formName, 'move');    }    public function actionDelete()    {        parent::baseAction($this->formName, 'delete');    }        public function actionList()    {        $userId = Yii::$app->factory->getuser()->userId;        $data = \lib\models\BuddyGroup::getList( $userId );        if($data) {            $this->success( $data );        } else {            $this->error('',404);        }    }}