<?phpnamespace api\controllers;use Yii;use lib\channel\liveTelecastRanking;//直播视频分类class ChannelClassifyController extends BasisController{    //关注列表    public function actionFollow()    {        $user_id = Yii::$app->factory->getuser()->userId;        $ranking = intval(Yii::$app->getRequest()->post('ranking'));        $this->success(['list' => liveTelecastRanking::followlist($ranking, $user_id,50)]);    }    //热门列表    public function actionHot()    {        $ranking = intval(Yii::$app->getRequest()->post('ranking'));        $this->success(['list' => liveTelecastRanking::hotlist($ranking,50)]);    }    //比赛列表    public function actionMatch()    {        $ranking = intval(Yii::$app->getRequest()->post('ranking'));        $this->success(['list' => liveTelecastRanking::matchlist($ranking,50)]);    }}