<?phpnamespace api\controllers;//积分类class CreditsController extends BasisController{    //分享接口添加积分    public  function actionSharing(){        $user_id=\Yii::$app->factory->getuser()->userId;        $creditsClass=\Yii::$app->factory->getCredits($user_id);        if ($creditsClass->sharing()){            return $this->success();        }else{            return $this->error();        }    }}