<?phpnamespace api\controllers;use lib\news\HotHeadlines;use Yii;use lib\models\HotHeadlinesType;use lib\models\HotHeadlines as HotHeadlinesModel;use yii\extend\AdCommon;class HotheadlinesController extends BasisController{    //热点头点分类    public function actionTypelist()    {        $this->success(['list' => HotHeadlinesType::getValidList() ]);    }    //新闻列表    public function actionNewslist()    {        $id = intval(Yii::$app->getRequest()->post('type'));        if(!$id) {            return $this->error('类型参数错误,请重新输入!');        }        $page = AdCommon::pageParameterId();        $data = HotHeadlinesModel::getList($page);        $this->success( $data );    }}