<?phpnamespace api\controllers;use Yii;use lib\models\HotHeadlinesType;class HotheadlinesController extends BasisController{    //热点头点分类    public function actionTypelist()    {        $result = HotHeadlinesType::getCacheList();        $new_result = [];        foreach($result as $row) {            if($row['state']) {                $new_result[] = [                    'iid' => $row['iid'],                    'name' => $row['name'],                ];            }        }        $this->success(['list' => $new_result]);    }    //新闻列表    public function actionNewslist()    {    }}