<?phpnamespace api\controllers;use Yii;use lib\models\Member;//会员控制器class MemberController extends BasisController{    //可购买的会员列表    public function actionList()    {        $result = Member::getCacheList();        foreach( $result as $key => $row ) {            unset( $row['operates'] );            unset( $row['is_default'] );            $result[$key] = $row;        }        $this->success(['list' => $result]);    }}