<?phpnamespace api\controllers;use Yii;use lib\forms\NoteForm;use lib\forms\EmailForm;use yii\extend\AdCommon;class NoteController extends BasisController{    public function actionRegister()    {        $usrename = Yii::$app->getRequest()->post('username');        if( AdCommon::isEmail( $usrename )) {            $model = new EmailForm();        } elseif( AdCommon::isMobile( $usrename ) ) {            $model = new NoteForm();        } else {            $this->error('帐号需为手机号码,或邮箱!');        }        $model->setScenario('register');        $model->username = $usrename;        if( $model->register() ) {            $this->success('','验证码发送成功,有效时间'.$model->validMinute.'分钟');        } else {            $this->error( $this->modelMessage( $model->errors ) );        }    }}