<?phpnamespace api\controllers;use Yii;class OtherController extends BasisController{    //覆盖父类init,取消验证    public function init(){    }    public function actionBannerNotice()    {        $query = \lib\models\Ads::getCacheList();        $list = [];        $keyName = [1=>'banner', 2=>'hot_ping', 3=>'hot_yi'];        foreach($query as $row) {            if(in_array($row['type'],[1,2,3])) {                $type = $row['type'];                unset($row['type']);                unset($row['id']);                unset($row['addtime']);                unset($row['listorder']);                $list[$keyName[$type]][] = $row;            }        }        $this->success($list);    }}