<?phpnamespace api\controllers;use lib\channel\rankingHour;use lib\channel\rankingMonth;use lib\channel\rankingWeek;use lib\channel\rankingDay;use lib\channel\matchRanking;use lib\channel\ranking;use Yii;class RankingController extends BasisController{    public function actionHour()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list'=>rankingHour::list($userid)]);    }    public function actionDay()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list'=>rankingDay::list($userid)]);    }    public function actionWeek()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list'=>rankingWeek::list($userid)]);    }    public function actionMonth()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list'=>rankingMonth::list($userid)]);    }    public function actionMatch()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list' => matchRanking::list($userid, 66)]);    }    public function actionAlways()    {        $userid = Yii::$app->factory->getuser()->userId;        $this->success(['list' => ranking::list($userid)]);    }}