<?phpnamespace api\controllers;use Yii;use yii\web\Controller;use lib\models\AppUpgrade;class SystemController extends BasisController{    public function init() {    }    //查最新版本接口    public function actionVersion()    {        $client = Yii::$app->getRequest()->post('client') ? 1 : 0;        $result = AppUpgrade::getNewVersion($client);        if($result) {            $this->success($result);        } else {            $this->error('没找到版本');        }    }    //检测是否是正式服务器    public function actionFormal()    {        $version = Yii::$app->getRequest()->post('version');        $formal = $version > 1.1 ? '1' : '0';        $this->success(['formal'=> $formal]);    }}