<?phpnamespace api\controllers;use Yii;use yii\web\Controller;use lib\models\AppUpgrade;class SystemController extends BasisController{    public function init() {    }    public function actionVersion()    {        $client = Yii::$app->getRequest()->post('client') ? 1 : 0;        $result = AppUpgrade::getNewVersion($client);        if($result) {            $this->success($result);        } else {            $this->error('没找到版本');        }    }}