<?phpnamespace api\controllers;use yii\extend\AdCommon;use lib\forms\UserForm;use Yii;class UserController extends BasisController{    public function actionRegister()    {        $model = new UserForm();        $model->setScenario('register');        if ($model->load(Yii::$app->request->post())) {            if( $model->register() ) {                $this->success();            } else {                $this->error($model);            }                    } else {            $this->error('数据加载错误!');        }    }    public function actionLogin()    {        $model = new UserForm();        $model->setScenario('login');        if ($model->load(Yii::$app->request->post())) {            if( $result = $model->login() ) {                $this->success( $result );            } else {                $this->error($model);            }        } else {            $this->error('数据加载错误!');        }    }    public function actionChangepassword()    {        $model = new UserForm();        $model->setScenario('changepassword');        if ($model->load(Yii::$app->request->post())) {            if( $model->changepassword() ) {                $this->success();            } else {                $this->error($model);            }        } else {            $this->error('数据加载错误!');        }    }    public function actionLogout()    {        if( HUANG_JING == 1 ) {            Yii::$app->factory->getuser()->logout();            $this->success();        } else {            Yii::$app->getSession()->destroy();            $this->success();        }    }    public function actionTest()    {    }}