<?phpnamespace api\controllers;use yii\extend\AdCommon;use lib\forms\UserForm;use lib\models\UserWithdrawDeposit;use Yii;class UserController extends BasisController{    //注册    public function actionRegister()    {        parent::baseAction('\lib\forms\UserForm', 'register');    }        //登入    public function actionLogin()    {        parent::baseAction('\lib\forms\UserForm', 'login');    }    //修改密码    public function actionChangepassword()    {        parent::baseAction('\lib\forms\UserForm', 'changepassword');    }    //退出    public function actionLogout()    {        if( HUANG_JING == 1 ) {            Yii::$app->factory->getuser()->logout();            $this->success();        } else {            Yii::$app->getSession()->destroy();            $this->success();        }    }    //更新信息    public function actionUpdateinfo()    {        parent::baseAction('\lib\forms\UserForm', 'updateinfo');    }    //取用户信息    public function actionGetinfo()    {        $this->success(Yii::$app->factory->getuser()->getinfo());    }    public function actionTest()    {        $result = \lib\nodes\Notice::send([            'from' => '13043434556',            'msgtype' => '0',            'to' => '18085195871',            'attach' => json_encode([                'title' => '充值成功',                'time' => date('Y-m-d H:i:s'),                'content' => '1000钻石',                'describe' => ['付款帐户:联联平台','交易对象:我的现金帐户']            ])        ]);        var_dump($result);            }}