<?phpnamespace api\controllers;use yii\extend\AdCommon;use lib\forms\UserForm;use Yii;class UserController extends BasisController{    public function actionRegister()    {        parent::baseAction('\lib\forms\UserForm', 'register');    }    public function actionLogin()    {        $model = new UserForm();        $model->setScenario('login');        if ($model->load(Yii::$app->request->post())) {            if( $result = $model->login() ) {                $this->success( $result );            } else {                $this->error($model);            }        } else {            $this->error('数据加载错误!');        }    }    public function actionChangepassword()    {        parent::baseAction('\lib\forms\UserForm', 'changepassword');    }    public function actionLogout()    {        if( HUANG_JING == 1 ) {            Yii::$app->factory->getuser()->logout();            $this->success();        } else {            Yii::$app->getSession()->destroy();            $this->success();        }    }    //更新信息    public function actionUpdateinfo()    {        parent::baseAction('\lib\forms\UserForm', 'updateinfo');    }    //取用户信息    public function actionGetinfo()    {        $this->success(Yii::$app->factory->getuser()->getinfo());    }}