<?phpnamespace api\controllers;use yii\extend\AdCommon;use lib\forms\UserForm;use lib\models\User;use lib\models\UserWithdrawDeposit;use Yii;use lib\models\Tree as TreeModel;class UserController extends BasisController{    public function actionTest()    {        $tree = new Tree();        $tree->list(1);        echo 'ok';    }}class Tree{    public function list($id)    {        $model =  TreeModel::findOne($id);        echo $model->name.'<br />';        $pid   = $model->iid;        $sql = "select * from tree where pid=$pid";        $data = Yii::$app->getDb()->createCommand($sql)->queryAll();        foreach ($data as $k => $v){            $this->list($v['iid']);        }    }}