<?phpnamespace api\controllers;use Yii;use yii\extend\AdCommon;use lib\models\UserNotice;//用户通知信息class UsernoticeController extends BasisController{    public function actionList()    {        $rq = Yii::$app->getRequest();        $isNext = $rq->post('isNext');        $type = $rq->post('type');        if(!in_array($type, ['wallet', 'buddy'])) {            $this->error('类型错误');        }        $uid = Yii::$app->factory->getuser()->userId;        $data = UserNotice::list(['user_id'=>$uid, 'type'=>$type], $isNext);        $this->success(['list' => $data]);    }    public function actionListios()    {        $rq = Yii::$app->getRequest();        $isNext = $rq->post('isNext');        $type = $rq->post('type');        if(!in_array($type, ['wallet', 'buddy'])) {            $this->error('类型错误');        }        $uid = Yii::$app->factory->getuser()->userId;        $data = UserNotice::list(['user_id'=>$uid, 'type'=>$type], $isNext);        foreach($data as $key => $row) {            $data[$key]['content'] = json_decode($row['content']);        }        $this->success(['list' => $data]);    }}