<?phpnamespace api\controllers;use Yii;use yii\extend\AdCommon;use lib\forms\UserVerifyInfoForm;//验证信息控制器class UserverifyController extends BasisController{    //发送验证信息    public function actionAdduser()    {        parent::baseAction('\lib\forms\UserVerifyInfoForm','adduser');    }        public function actionList()    {        $model = new UserVerifyInfoForm();        $model->user_id = \Yii::$app->factory->getuser()->userId;        $data = $model->getList();        if($data) {            $this->success(['list' => $data]);        } else {            $this->error('没有数据',404);        }    }    //删除    public function actionDelete()    {        parent::baseAction('\lib\forms\UserVerifyInfoForm','delete');    }    //拒绝    public function actionRefuse()    {        parent::baseAction('\lib\forms\UserVerifyInfoForm','refuse');    }    //同意    public function actionAgree()    {        parent::baseAction('\lib\forms\UserVerifyInfoForm','agree');    }}