<?phpnamespace api\controllers;use Yii;use yii\extend\AdCommon;use lib\models\VipVideo;class VipvideoController extends BasisController{    public function actionList()    {        $isNext = Yii::$app->getRequest()->post('isNext');        $userId = Yii::$app->factory->getuser()->userId;        $this->success(['list'=>VipVideo::list($userId,$isNext)]);    }    public function actionGetinfo()    {        $id = Yii::$app->getRequest()->post('id');        if(!$id) {            $this->error('请上传id');        }        $result = \lib\wyim\video::getinfo($id);        if($result)        {            $this->success($result);        }        else        {            $this->error('获取数据失败!');        }    }}