<?phpnamespace app\controllers;use yii\web\Controller;use Yii;use yii\extend\AdCommon;class ChannelController extends Controller{    //刷新直播状态    public function actionRefurbish()    {        \lib\wyim\channel::refurbish();    }    public function actionTakepart()    {        $userid = Yii::$app->getRequest()->post('uid');        if(!$userid || !$userModel = \lib\models\User::findOne($userid)) {            echo json_encode(['code'=>400, 'msg'=>'参数错误,或用户不存在']);            die;        }                \lib\channel\matchRanking::add_takepart($userid);        echo json_encode(['code'=>200, 'msg'=>'ok']);        die;    }}