<?phpnamespace app\controllers;use yii\web\Controller;use lib\channel\rankingHour;use lib\channel\rankingMonth;use lib\channel\rankingWeek;use lib\channel\rankingDay;use lib\channel\matchRanking;use lib\channel\liveTelecastRanking;use Yii;class RankingController extends Controller{    //刷新小时    public function actionRefurbishHour()    {        \lib\models\CodeLog::addlog('auto clear hour','');        rankingHour::clear();    }    //刷新天    public function actionRefurbishDay()    {        \lib\models\CodeLog::addlog('auto clear day','');        rankingDay::clear();    }    //刷新星期    public function actionRefurbishWeek()    {        \lib\models\CodeLog::addlog('auto clear week','');        rankingWeek::clear();    }    //刷新月    public function actionRefurbishMonth()    {        \lib\models\CodeLog::addlog('auto clear month','');        rankingMonth::clear();    }    //载入比赛数据    public function actionLoadMatch()    {        matchRanking::load();    }    public function actionClear()    {        \lib\models\CodeLog::addlog('auto clear all','');        \lib\channel\rankingHour::clear();        \lib\channel\rankingDay::clear();        \lib\channel\rankingWeek::clear();        \lib\channel\rankingMonth::clear();        \lib\channel\ranking::clear();    }    //添加比赛用户    public function actionAddUser()    {        $userid = intval(Yii::$app->getRequest()->post('userid'));        if($userid) {            matchRanking::addUser($userid);            echo json_encode(['code'=>200,'message'=> '参数错误']);        } else {            echo json_encode(['code'=>400,'message'=> '参数错误']);        }    }}