<?phpnamespace lib\nodes;use Yii;//工厂类class FactoryNode extends \yii\base\Component{    private $userNode;      //用户类    private $memberNode;    //会员类        //取用户类    public function getuser( $user_id = 0 )    {        if(!$this->userNode) {            $this->userNode = $this->createuser($user_id);        }        return $this->userNode;    }    public function createuser($user_id)    {        $class =  new \lib\nodes\UserNode();        $class->userId = $user_id;        return $class;    }    //取会员类    public function getmember()    {        if(!$this->memberNode) {            $this->memberNode = new \lib\nodes\MemberNode();        }        return $this->memberNode;    }        }?>