<?phpnamespace lib\nodes;use Yii;use lib\models\User;//工厂类class FactoryNode extends \yii\base\Component{    private $userNode;      //用户类    private $memberNode;    //会员类    private $wealths;        //财富类    //取用户类    public function getuser( $user_id = 0 )    {        if(!$this->userNode) {            $this->userNode = $this->createuser($user_id);        }        return $this->userNode;    }    public function createuser($user_id)    {        $class =  new \lib\nodes\UserNode();        $class->userId = $user_id;        return $class;    }    //取会员类    public function getmember()    {        if(!$this->memberNode) {            $this->memberNode = new \lib\nodes\MemberNode();        }        return $this->memberNode;    }    //取财富    public function getwealth($type,User $userModel)    {        if(isset($this->wealths[$type])) {            $wealthClass = $this->wealths[$type];        } else {            $method = 'get'.ucfirst($type).'Wealth';            if(method_exists($this,$method)) {                $wealthClass = $this->$method();                $this->wealths[$type] = $wealthClass;            } else {                throw new \Exception('type错误');            }        }        $wealthClass->userModel = $userModel;        return $wealthClass;    }    //创建钻石类    public function getDiamondWealth(){        return new \lib\wealth\Diamond();    }    //创建钱包类    public function getWalletWealth(){        return new \lib\wealth\Wallet();    }        }?>