<?phpnamespace lib\nodes;class Rpc{    public static $error;    protected static $register = [        'User' => [            'class' => '\lib\models\User',            'method' => [                'findOne' => 'static',                'extra' => 'toArray',            ],        ]    ];    public static function execute($string)    {        $class = $string['class'];        $methodName = $string['method'];        $parameter = $string['parameter'];        $node = static::$register[$class] ?? '';        if(!$node) {            static::$error = '该类不存在';            return false;        }        $mothodType = $node['method'][$methodName] ?? '';        if(!$mothodType) {            static::$error = '方法不存在';            return false;        }        if($mothodType == 'static') {            $result = $node['class']::$methodName($parameter);        } else {            $class = new $node['class'];            $result = $class->$methodName($parameter);        }        if(!$result) {            static::$error = '数据不存在';            return false;        }        $extra = $node['method']['extra'] ?? '';        if($extra) {            $result = $result->$extra();        }        return $result;            }        }