<?phpnamespace lib\traits;trait formEngineTrait{    public static $error;    public static function execute($method, $data=[])    {        $form = new static();        $form->setScenario($method);        if($data) {            foreach($data as $key => $value) {                $form->$key = $value;            }        }        $result = $form->$method();        if(!$result) {            static::$error = \yii\extend\AdCommon::modelMessage($form->errors);        }            return $result;    }}