<?phpnamespace lib\wealth;use lib\models\User;use lib\models\Setting;use Yii;class BaseWealth{    public $userModel;          //指向用户模型    protected $fieldName;       //对应用户字段    protected $logClassName;    //对应的日志文件名    public $error = '';        //减少    public function sub( $data )    {        //用户还能增加积分        $fieldName = $this->fieldName;        if($this->userModel->$fieldName < $data['number']) {            $this->error = \Yii::t('common',$fieldName).'数量不足';            return false;        }        $this->userModel->$fieldName -= $data['number'];        $data['number'] = 0 - $data['number'];        $t = \Yii::$app->getDb()->beginTransaction();        if(!$this->userModel->save()) {            $t->rollBack();            return false;        }        //添加到日志中        $data['user_id'] = $this->userModel->iid;        $logClassName = $this->logClassName;        $result =$logClassName::add( $data );        if($result) {            $t->commit();        } else {            $t->rollBack();        }        return $result;    }    //增加    public function add( $data )    {        //用户还能增加积分        $fieldName = $this->fieldName;        $this->userModel->$fieldName += $data['number'];                $t = \Yii::$app->getDb()->beginTransaction();        if(!$this->userModel->save()) {            $t->rollBack();            return false;        }        //添加到日志中        $data['user_id'] = $this->userModel->iid;        $logClassName = $this->logClassName;        $result =$logClassName::add( $data );        if($result) {            $t->commit();        } else {            $t->rollBack();        }        return $result;    }    //添加奖励    public function addReward( $data ) {        $t = \Yii::$app->getDb()->beginTransaction();        if($this->userModel->reward_count <  Setting::keyTovalue('max_reward')) {            $this->userModel->reward_count += $data['number'];            if(!$this->userModel->save()) {                $t->rollBack();                return false;            }            if(!Yii::$app->factory->getwealth('wallet', $this->userModel)->add($data)) {                $t->rollBack();                return false;            }            $t->commit();            return true;        }        $walletdata = $diamonddata = $data;        $walletdata['number'] = $walletdata['number'] * Setting::keyTovalue('max_reward_money') / 100;        $diamonddata['number'] = $diamonddata['number'] * Setting::keyTovalue('max_reward_diamond') / 100;        if(!Yii::$app->factory->getwealth('wallet', $this->userModel)->add($walletdata)) {            $t->rollBack();            return false;        }        if(!Yii::$app->factory->getwealth('diamond', $this->userModel)->add($diamonddata)) {            $t->rollBack();            return false;        }        $t->commit();        return true;    }}