<?phpnamespace lib\wyim;use lib\models\User;use yii\extend\AdCommon;class chatroom extends wy{    //创建聊天室    public static function create($creator, $name)    {        static::$url = 'https://api.netease.im/nimserver/chatroom/create.action';        $result = static::send([            'creator' => $creator,            'name' => $name,        ]);                if($result) {            return $result->chatroom;        } else {            return false;        }    }    //发送聊天室信息    public static function sendGift($roomid, User $userModel, $gift_type, $gift_number)    {        static::$url = 'https://api.netease.im/nimserver/chatroom/sendMsg.action';        $msgId = md5(AdCommon::randomkeys().time().mt_rand());        $result = static::send([            'roomid' => $roomid,            'msgId' => $msgId,            'fromAccid' => '13043434556',            'msgType' => 0,            'ext' => json_encode([                'type'=>'2',                'userinfo' => [                    'nickname' => $userModel->nickname,                    'vip' => $userModel->vip_type ? '1' : '0',                    'head' => $userModel->get_head_url($userModel->head),                ],                'result' => [                    'gift_type' => $gift_type,                    'gift_number' => $gift_number,                ]            ]),        ]);                if($result) {            return true;        } else {            return false;        }    }}