<?phpnamespace lib\wyim;use lib\models\User;class wy{    protected static $curl = null;    public static $error = '';    public static $url = '';    public static function getCurl()    {        if(!static::$curl) {            static::$curl = new curl();        }        return static::$curl;    }    protected static function send($data) {        $result = static::getCurl()->posts($data, static::$url);        if( isset($result->code) && $result->code == 200 ) {            return $result;        } else {            static::$error = $result->desc ?? ($result->msg ?? '');            return false;        }    }    protected static function   sendJson($data) {        $result = static::getCurl()->postsJson($data, static::$url);        if( isset($result->code) && $result->code == 200 ) {            return $result;        } else {            static::$error = $result->desc ?? ($result->msg ?? '');            return false;        }    }}