<?phpnamespace lib\wyim;use lib\models\User;class wyim extends wy{    //创建帐号    public static function createAccid($user)    {        $data = [            'accid' => $user->llaccounts,            'name' => $user->nickname,            'icon' => $user->head,        ];        static::$url = 'https://api.netease.im/nimserver/user/create.action';        $result = static::send($data);        return $result ? $result->info : false;    }    //更新用户资料    public static function updateUinfo($user)    {        $data = [            'icon' => \lib\nodes\UserNode::get_head_url($user->head),            'name' => $user->nickname,            'accid' => $user->wy_accid,        ];        static::$url = 'https://api.netease.im/nimserver/user/updateUinfo.action';        return static::send($data);    }    //更新令牌    public static function refreshToken($user)    {        static::$url = 'https://api.netease.im/nimserver/user/refreshToken.action';        $data = ['accid'=>$user->llaccounts];        $result = static::send($data);        return $result ? $result->info : false;    }    //发送信息    public static function sendNotice($data)    {        static::$url = 'https://api.netease.im/nimserver/msg/sendAttachMsg.action';        return static::send($data);    }    //添加好友    public static function addBuddy($user, $buddy){                static::$url = 'https://api.netease.im/nimserver/friend/add.action';        $data = [            'accid' => $user->wy_accid,            'faccid' => $buddy->wy_accid,            'type' => 3,            'msg' => '同意添加好友',        ];        return static::send($data);    }    //删除好友    public static function deleteBuddy(User $userModel,User $buddyModel){        $data = [            'accid' => $userModel->wy_accid,            'faccid' => $buddyModel->wy_accid,        ];        static::$url = 'https://api.netease.im/nimserver/friend/delete.action';        return static::send($data);    }    //创建群    public static function createTeam($data){        $url = 'https://api.netease.im/nimserver/team/create.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return $result->tid;        } else {            static::$error = json_encode($result);            return false;        }    }    //加人入群    public static function addUserToTeam($data) {        $url = 'https://api.netease.im/nimserver/team/add.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return true;        } else {            static::$error = json_encode($result);            return false;        }    }    //踢人出群    public static function kickTeam($data) {        $url = 'https://api.netease.im/nimserver/team/kick.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return true;        } else {            static::$error = json_encode($result);            return false;        }    }    public static function buddylist($data) {        $url = 'https://api.netease.im/nimserver/friend/get.action';        $result = static::getCurl()->posts($data, $url);        return $result;    }    //创建直播频道    public static function createCl($name)    {        static::$url = 'https://vcloud.163.com/app/channel/create';        $result = static::sendJson([            'name' => $name,            'type' => 0,        ]);        if($result) {            return $result->ret;        } else {            return false;        }    }}