<?phpnamespace lib\wyim;class wyim{    private static $curl = null;    public static $error = '';    public static function getCurl()    {        if(!static::$curl) {            static::$curl = new curl();        }        return static::$curl;    }    public static function createAccid($user)    {        if(empty($user['username'])) {        }        $url = 'https://api.netease.im/nimserver/user/create.action';        $data = [            'accid' => $user['username'],            'name' => $user['nickname'] ?? '',            'icon' => $user['head'] ?? '',        ];        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code ==200 ) {            //成功            return $result->info;        } else {            static::$error = json_encode($result);            return false;        }    }}