<?phpnamespace lib\wyim;class wyim{    private static $curl = null;    public static $error = '';    public static function getCurl()    {        if(!static::$curl) {            static::$curl = new curl();        }        return static::$curl;    }    //创建帐号    public static function createAccid($user)    {        $url = 'https://api.netease.im/nimserver/user/create.action';        $result = static::getCurl()->posts($user, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return $result->info;        } else {            static::$error = $result;            return false;        }    }    //更新用户资料    public static function updateUinfo($data)    {        $url = 'https://api.netease.im/nimserver/user/updateUinfo.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return true;        } else {            static::$error = json_encode($result);            return false;        }    }    //更新令牌    public static function refreshToken($data)    {        $url = 'https://api.netease.im/nimserver/user/refreshToken.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return $result->info;        } else {            static::$error = json_encode($result);            return false;        }    }    //发送信息    public static function sendNotice($data)    {        $url = 'https://api.netease.im/nimserver/msg/sendAttachMsg.action';        $result = static::getCurl()->posts($data, $url);        if( isset($result->code) && $result->code == 200 ) {            //成功            return true;        } else {            static::$error = json_encode($result);            return false;        }    }}